package cuchaz.ships;

import static org.junit.Assert.*;
import org.junit.Test;

import cuchaz.modsShared.perf.Profiler;

public class TestShipLag
{
	private static final String BigShip = "";
	
	@Test
	public void testShipGeometry( )
	throws Exception
	{
		new MinecraftRunner( )
		{
			@Override
			public void onRun( )
			throws Exception
			{
				System.out.println( "Geometry:" );
				Profiler.reset();
				
				BlocksStorage shipBlocks = new BlocksStorage();
				shipBlocks.readFromString( BigShip );
				
				// time it
				long time = System.currentTimeMillis();
				shipBlocks.getGeometry();
				long diff = System.currentTimeMillis() - time;
				
				System.out.println( String.format( "Time: %.2fs", diff/1000.0 ) );
				System.out.println( Profiler.getReport() );
				
				// this shouldn't take more than 0.1 seconds
				assertTrue( diff <= 100 );
			}
		}.run();
	}
	
	@Test
	public void testShipDisplacement( )
	throws Exception
	{
		new MinecraftRunner( )
		{
			@Override
			public void onRun( )
			throws Exception
			{
				System.out.println( "Displacement:" );
				Profiler.reset();
				
				BlocksStorage shipBlocks = new BlocksStorage();
				shipBlocks.readFromString( BigShip );
				
				// time it
				long time = System.currentTimeMillis();
				shipBlocks.getDisplacement();
				long diff = System.currentTimeMillis() - time;
				
				System.out.println( String.format( "Time: %.2fs", diff/1000.0 ) );
				System.out.println( Profiler.getReport() );
				
				// this shouldn't take more than 0.1 seconds
				assertTrue( diff <= 100 );
			}
		}.run();
	}
}
